contracts/MaidCafe.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.5;

import "./interfaces/IMaidCafe.sol";
import "./interfaces/IUniswapV2Router02.sol";
import "./interfaces/IWETH.sol";
import "@openzeppelin/contracts/token/ERC20/ERC20.sol";
import "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";
import "@openzeppelin/contracts/access/Ownable.sol";

contract MaidCafe is IMaidCafe, ERC20("Maid Cafe", "$OMU"), Ownable {
    using SafeERC20 for IERC20;
    IMaidCoin public immutable override maidCoin;
    IWETH public immutable WETH;

    constructor(IMaidCoin _maidCoin, IWETH _WETH) {
        maidCoin = _maidCoin;
        WETH = _WETH;
    }

    receive() external payable {}

    // Enter the Maid Caf. Pay some $MAIDs. Earn some shares.
    // Locks $MAID and mints $OMU (Omurice)
    function enter(uint256 _amount) public override {
        // Gets the amount of $MAID locked in the Maid Caf
        uint256 totalMaidCoin = maidCoin.balanceOf(address(this));
        // Gets the amount of $OMU in existence
        uint256 totalShares = totalSupply();
        // If no $OMU exists, mint it 1:1 to the amount put in
        if (totalShares == 0 || totalMaidCoin == 0) {
            _mint(msg.sender, _amount);
        }
        // Calculate and mint the amount of $OMU the $MAID is worth. The ratio will change overtime, as $OMU is burned/minted and $MAID deposited + gained from fees / withdrawn.
        else {
            uint256 what = (_amount * totalShares) / totalMaidCoin;
            _mint(msg.sender, what);
        }
        // Lock the $MAID in the Maid Caf
        IERC20(address(maidCoin)).safeTransferFrom(msg.sender, address(this), _amount);
        emit Enter(msg.sender, _amount);
    }

    function enterWithPermit(
        uint256 _amount,
        uint256 deadline,
        uint8 v,
        bytes32 r,
        bytes32 s
    ) external override {
        maidCoin.permit(msg.sender, address(this), _amount, deadline, v, r, s);
        enter(_amount);
    }

    // Leave the Maid Caf. Claim back your $MAIDs.
    // Unlocks the staked + gained $MAID and burns $OMU
    function leave(uint256 _share) external override {
        // Gets the amount of $OMU in existence
        uint256 totalShares = totalSupply();
        // Calculates the amount of $MAID the $OMU is worth
        uint256 what = (_share * maidCoin.balanceOf(address(this))) / totalShares;
        _burn(msg.sender, _share);
        IERC20(address(maidCoin)).safeTransfer(msg.sender, what);
        emit Leave(msg.sender, _share);
    }

    function swap(
        address token,
        IUniswapV2Router02 router,
        address[] calldata path,
        uint256 amountOutMin,
        uint256 deadline
    ) external onlyOwner {
        require(token != address(maidCoin), "MaidCafe: Invalid token");
        require(path[path.length - 1] == address(maidCoin), "MaidCafe: Invalid path");
        uint256 amountIn;
        if (token == address(0)) {
            require(path[0] == address(WETH), "MaidCafe: Invalid path");
            amountIn = address(this).balance;
            require(amountIn > 0, "MaidCafe: Invalid amount");
            WETH.deposit{value: amountIn}();
        } else {
            require(path[0] == token, "MaidCafe: Invalid path");
            amountIn = IERC20(token).balanceOf(address(this));
            require(amountIn > 0, "MaidCafe: Invalid amount");
        }
        IERC20(path[0]).approve(address(router), amountIn);
        router.swapExactTokensForTokens(amountIn, amountOutMin, path, address(this), deadline);
    }
}

contracts/interfaces/IUniswapV2Router02..content:
// SPDX-License-Identifier: MIT
pragma solidity >=0.6.12;

interface IUniswapV2Router01 {
    function factory() external pure returns (address);

    function WETH() external pure returns (address);

    function addLiquidity(
        address tokenA,
        address tokenB,
        uint256 amountADesired,
        uint256 amountBDesired,
        uint256 amountAMin,
        uint256 amountBMin,
        address to,
        uint256 deadline
    )
        external
        returns (
            uint256 amountA,
            uint256 amountB,
            uint256 liquidity
        );

    function addLiquidityETH(
        address token,
        uint256 amountTokenDesired,
        uint256 amountTokenMin,
        uint256 amountETHMin,
        address to,
        uint256 deadline
    )
        external
        payable
        returns (
            uint256 amountToken,
            uint256 amountETH,
            uint256 liquidity
        );

    function removeLiquidity(
        address tokenA,
        address tokenB,
        uint256 liquidity,
        uint256 amountAMin,
        uint256 amountBMin,
        address to,
        uint256 deadline
    ) external returns (uint256 amountA, uint256 amountB);

    function removeLiquidityETH(
        address token,
        uint256 liquidity,
        uint256 amountTokenMin,
        uint256 amountETHMin,
        address to,
        uint256 deadline
    ) external returns (uint256 amountToken, uint256 amountETH);

    function removeLiquidityWithPermit(
        address tokenA,
        address tokenB,
        uint256 liquidity,
        uint256 amountAMin,
        uint256 amountBMin,
        address to,
        uint256 deadline,
        bool approveMax,
        uint8 v,
        bytes32 r,
        bytes32 s
    ) external returns (uint256 amountA, uint256 amountB);

    function removeLiquidityETHWithPermit(
        address token,
        uint256 liquidity,
        uint256 amountTokenMin,
        uint256 amountETHMin,
        address to,
        uint256 deadline,
        bool approveMax,
        uint8 v,
        bytes32 r,
        bytes32 s
    ) external returns (uint256 amountToken, uint256 amountETH);

    function swapExactTokensForTokens(
        uint256 amountIn,
        uint256 amountOutMin,
        address[] calldata path,
        address to,
        uint256 deadline
    ) external returns (uint256[] memory amounts);

    function swapTokensForExactTokens(
        uint256 amountOut,
        uint256 amountInMax,
        address[] calldata path,
        address to,
        uint256 deadline
    ) external returns (uint256[] memory amounts);

    function swapExactETHForTokens(
        uint256 amountOutMin,
        address[] calldata path,
        address to,
        uint256 deadline
    ) external payable returns (uint256[] memory amounts);

    function swapTokensForExactETH(
        uint256 amountOut,
        uint256 amountInMax,
        address[] calldata path,
        address to,
        uint256 deadline
    ) external returns (uint256[] memory amounts);

    function swapExactTokensForETH(
        uint256 amountIn,
        uint256 amountOutMin,
        address[] calldata path,
        address to,
        uint256 deadline
    ) external returns (uint256[] memory amounts);

    function swapETHForExactTokens(
        uint256 amountOut,
        address[] calldata path,
        address to,
        uint256 deadline
    ) external payable returns (uint256[] memory amounts);

    function quote(
        uint256 amountA,
        uint256 reserveA,
        uint256 reserveB
    ) external pure returns (uint256 amountB);

    function getAmountOut(
        uint256 amountIn,
        uint256 reserveIn,
        uint256 reserveOut
    ) external pure returns (uint256 amountOut);

    function getAmountIn(
        uint256 amountOut,
        uint256 reserveIn,
        uint256 reserveOut
    ) external pure returns (uint256 amountIn);

    function getAmountsOut(uint256 amountIn, address[] calldata path) external view returns (uint256[] memory amounts);

    function getAmountsIn(uint256 amountOut, address[] calldata path) external view returns (uint256[] memory amounts);
}

interface IUniswapV2Router02 is IUniswapV2Router01 {
    function removeLiquidityETHSupportingFeeOnTransferTokens(
        address token,
        uint256 liquidity,
        uint256 amountTokenMin,
        uint256 amountETHMin,
        address to,
        uint256 deadline
    ) external returns (uint256 amountETH);

    function removeLiquidityETHWithPermitSupportingFeeOnTransferTokens(
        address token,
